import logo from './logo.svg';
import './App.css';
import { useState } from 'react';
import { click } from '@testing-library/user-event/dist/click';

function Counter() {
    const [count, setcount] = useState([])
    const [item ,setitem] = useState("")
    const [mode,setmode] = useState(false)

    const andre = function(e){
      setitem([e.target.value])

    }

    const add = function(e){

      setcount([...count , item])
      setitem("")

    }
const deleteing = function(){
    setcount(count.slice(0,count.length-1))
}

const editmodeon = function(){
  setmode(true)
}
const editmodeoff = function(){
  setmode(false)
}

    return (
      <>

      <input onChange={andre} value={item} ></input> 
      <button onClick={add}>Add</button>
      <button onClick={deleteing}>Add</button>
      <ul>{count.map(function(item,index){
        if(mode && item.key == item.id){
          return (
           <input onClick={editmodeoff} key={index}></input>
          )
        }
          return (<li key={index} id ={index} onClick={editmodeon}>{item}</li>) 
      })}</ul>

      </>
    );
}



export default Counter;
