import { useEffect, useState } from "react";
import "./App.css";



function App() {

const [getdata ,setgetdata] = useState([])
const[clicking,setclicking] = useState(-1)



const click = function(id){

    fetch("https://ghibliapi.herokuapp.com/films")
    .then(function(res){
      return res.json()
    })
    .then(function(json){
      setgetdata(json)
    })
  }




const clickingg = function(id){
 return function x(){

  setclicking(id)


}
}
console.log(clicking)
return (
  <div>
    <ul>
      {getdata.map(function (item,index){
          return (
            <li onClick={clickingg(index)} key={index}>{item.title}</li>
          )
      })}
    </ul>
    <ul>
    {getdata.map(function (item,index){
      if( clicking === index){

          return (  
            <div className="mirrorblok" key={Math.random()}>   
            <img src={item.image} className="nkar"></img>
              <ul >        
            <li onClick={clickingg} key={Math.random()} className="list">{  item.title}</li>
            <li onClick={clickingg} key={Math.random()} className="list">{  item.producer}</li>
            <li onClick={clickingg} key={Math.random()} className="list">{  item.director}</li>
            <li onClick={clickingg} key={Math.random()} className="list">{  item.release_date}</li>
            </ul>
            </div>
             )


          }
      })}
    </ul>

    <button onClick={click}>Get list</button>
  </div>
)
}

export default App;
