import { useEffect, useState } from "react";
import "./App.css";



function App() {
const [clicked,setclicked] = useState(0)
const [getdata ,setgetdata] = useState([])
const[clicking,setclicking] = useState()



const click = function(id){
  if(clicked>0){
    fetch("https://ghibliapi.herokuapp.com/films")
    .then(function(res){
      return res.json()
    })
    .then(function(json){
      setgetdata(json)
    })
  }
  setclicked(clicked+1)
  console.log(getdata)
}

const Clickingg = function(id){
 return useEffect(function x(){
  setclicking(id)
  console.log(clicking)
}[clicking]) 
}

return (
  <div>
    <ul>
      {getdata.map(function (item,index){
          return (
            <li onClick={Clickingg(index)} key={index}>{getdata[index].title + getdata[index].director  }</li>
          )
      })}
    </ul>
    <ul>
    {getdata.map(function (item,index){
      if(clicking && clicking == index){
          return (             
            <li onClick={Clickingg} key={index}>{getdata[clicking].director  }</li>
             )
      }


      })}
    </ul>
    <button onClick={click}>Get list</button>
  </div>
)
}

export default App;
