import { useEffect, useState } from "react";
import "./App.css";



function App() {

const [getdata ,setgetdata] = useState([])
const[clicking,setclicking] = useState([1,2,2])

useEffect(function(){
setclicking([0])
},[])

const click = function(id){

    fetch("https://ghibliapi.herokuapp.com/films")
    .then(function(res){
      return res.json()
    })
    .then(function(json){
      setgetdata(json)
    })
  }

  console.log(getdata)


const clickingg = function(id){
 return function x(){

  setclicking([...clicking,id])
  console.log(clicking);

}
}
console.log(clicking)
return (
  <div>
    <ul>
      {getdata.map(function (item,index){
          return (
            <li onClick={clickingg(index)} key={index}>{getdata[index].title + getdata[index].director  }</li>
          )
      })}
    </ul>
    <ul>
    {getdata.map(function (item,index){
      if(clicking == index){

          return (             
            <li onClick={clickingg} key={index}>{clicking.includes(index)? getdata[index].director :getdata[clicking].director }</li>
             )
      }


      })}
    </ul>

    <button onClick={click}>Get list</button>
  </div>
)
}

export default App;
