import { useEffect, useState } from "react";
import "./App.css";



function App() {
const [clicked,setclicked] = useState(0)
const [getdata ,setgetdata] = useState([])

const data = function(){
  if(clicked>0){
  fetch("https://ghibliapi.herokuapp.com/films")
  .then(function(res){
    return res.json()
  })
  .then(function(json){
    setgetdata([...getdata,json.title])
  })
}
}

const click = function(){
  setclicked(clicked+1)
}

return (
  <div>
    <ul>
    {getdata.map(function(item){
      if(clicked >0){
        return(
          <li>{item}</li>
        )
      }

    })}
    </ul>
    <button onClick={click}>Get list</button>
  </div>
)
}

export default App;
