import { useEffect, useState } from "react";
import "./App.css";



function App() {
const [clicked,setclicked] = useState(0)
const [getdata ,setgetdata] = useState([])
const[clicking,setclicking] = useState([])

const data = function(){
  if(clicked>0){
  fetch("https://ghibliapi.herokuapp.com/films")
  .then(function(res){
    return res.json()
  })
  .then(function(json){
    setgetdata(json)
  })
}
}

const click = function(id){
  if(clicked>0){
    fetch("https://ghibliapi.herokuapp.com/films")
    .then(function(res){
      return res.json()
    })
    .then(function(json){
      setgetdata(json)
    })
  }
  setclicked(clicked+1)
  console.log(getdata)
}

const clickingg = function(id){
  setclicking([id])
  console.log(clicking)
}

return (
  <div>
    <ul>
      {getdata.map(function (item,index){
          return (
            <li onClick={clickingg(item[0].id)} key={index}>{getdata[index].title + getdata[index].director  }</li>
          )
      })}
    </ul>
    {getdata.map(function (item,index){
      if(clicking>0 && clicking === index){
          return (
            <li onClick={clickingg} key={index}>{getdata[index].title + getdata[index].director  }</li>
          )
      }
      })}
    <ul>

    </ul>
    <button onClick={click}>Get list</button>
  </div>
)
}

export default App;
